package auth_test

import (
	"testing"
	"time"

	"github.com/golang-jwt/jwt/v5"
	"github.com/stretchr/testify/require"
	"github.com/tinh-tinh/auth/v2"
)

func Test_RS256(t *testing.T) {
	jwtService := auth.NewJwtRS(auth.JwtOptions{
		Alg:        jwt.SigningMethodRS256,
		PrivateKey: "",
		PublicKey:  "LS0tLS1CRUdJTiBQVUJMSUMgS0VZLS0tLS0KTUZ3d0RRWUpLb1pJaHZjTkFRRUJCUUFEU3dBd1NBSkJBTzVIKytVM0xrWC91SlRvRHhWN01CUURXSTdGU0l0VQpzY2xhRSs5WlFIOUNlaThiMXFFZnJxR0hSVDVWUis4c3UxVWtCUVpZTER3MnN3RTVWbjg5c0ZVQ0F3RUFBUT09Ci0tLS0tRU5EIFBVQkxJQyBLRVktLS0tLQ==",
		Exp:        time.Hour,
	})

	token, err := jwtService.Generate(jwt.MapClaims{
		"foo": "bar",
	})
	require.NoError(t, err)

	payload, err := jwtService.Verify(token)
	require.NoError(t, err)
	require.Equal(t, "bar", payload["foo"])

	_, err = jwtService.Verify("Abc")
	require.Error(t, err)

	jwtService2 := auth.NewJwtHS(auth.JwtOptions{
		Alg:    jwt.SigningMethodHS256,
		Secret: "",
		Exp:    1 * time.Second,
	})
	token, err = jwtService2.Generate(jwt.MapClaims{
		"foo": "bar",
	})
	require.Nil(t, err)

	_, err = jwtService.Verify(token)
	require.Error(t, err)

	time.Sleep(3 * time.Second)
	_, err = jwtService2.Verify(token)
	require.Error(t, err)

	claims, err := jwtService.Decode(token)
	require.Nil(t, err)
	require.Equal(t, "bar", claims["foo"])

	_, err = jwtService.Decode("bad.token")
	require.NotNil(t, err)

	claims, err = jwtService2.Decode(token)
	require.Nil(t, err)
	require.Equal(t, "bar", claims["foo"])

	_, err = jwtService2.Decode("bad.token")
	require.NotNil(t, err)
}

